import React, { useRef, useLayoutEffect } from 'react'
import styled from 'styled-components'
import gsap from 'gsap'
import ScrollTrigger from 'gsap/ScrollTrigger'
import MyCard from './MyCard'
import img1 from '../../assets/LessonTopics/1.png'
import img2 from '../../assets/LessonTopics/2.png'
import { Grid } from '@mui/material'

const Section = styled.section`
// min-height: 80vh;
width: 100%;
background-color: ${props => props.theme.body};
display: flex;
justify-content: center;
align-items: center;
position: relative;
flex-direction: column;
`
const CardContainer = styled.div`
width: 60%;
background-color: ${props => props.theme.body};
display: flex-start;
justify-content: center;
align-items: center;
position: relative;
padding: 3%;
`

const Topics = () => {

    const ref = useRef(null);
    gsap.registerPlugin(ScrollTrigger);
    useLayoutEffect(() => {
      
        let element = ref.current;
        ScrollTrigger.create({
            id: 'slideID',
            trigger: element,
            start: 'top top',
            end: 'bottom top',
            pin: true,
            pinSpacing: false,
            scrub: true,
        })
    
      return () => {
        ScrollTrigger.getById('slideID').kill();
      };
    }, [])
  return (
    <>
    <Section ref={ref}>
        <CardContainer>
            <Grid container spacing={3}>
                    <Grid item xs={12} sm={6} md={4} lg={3}><MyCard height={"300"} img={img1} alt={"1"} text={"A Brief History"} /></Grid>
                    <Grid item xs={12} sm={6} md={4} lg={3}><MyCard height={"300"} img={img2} alt={"1"} text={"A Brief History"} /></Grid>
                    <Grid item xs={12} sm={6} md={4} lg={3}><MyCard height={"300"} img={img1} alt={"1"} text={"A Brief History"} /></Grid>
                    <Grid item xs={12} sm={6} md={4} lg={3}><MyCard height={"300"} img={img1} alt={"1"} text={"A Brief History"} /></Grid>
                    <Grid item xs={12} sm={6} md={4} lg={3}><MyCard height={"300"} img={img1} alt={"1"} text={"A Brief History"} /></Grid>
                    <Grid item xs={12} sm={6} md={4} lg={3}><MyCard height={"300"} img={img1} alt={"1"} text={"A Brief History"} /></Grid>
                    <Grid item xs={12} sm={6} md={4} lg={3}><MyCard height={"300"} img={img1} alt={"1"} text={"A Brief History"} /></Grid>
                    <Grid item xs={12} sm={6} md={4} lg={3}><MyCard height={"300"} img={img1} alt={"1"} text={"A Brief History"} /></Grid>
                

            </Grid>
            


        </CardContainer>
    
    </Section>
    </>
  )
}

export default Topics
