import React from 'react'
import styled from 'styled-components'
import animationData from "../../assets/LottieJson/details/detail2.json";
import LottieAnimation from "../../lottieScripts/LottieAnimation";


const VideoContainer = styled.div`
width: 80%;
height:40vh;
`
const TextContainer = styled.div`
width: 80%;
height:20vh;
`

const Title = styled.h2`
font-size: ${(props) => props.theme.fontlg};
text-transform: capitalize;
width: 100%;
color: ${(props) => props.theme.text};
text-align: center;
// position: relative;
`;

const SubTitle = styled.h3`
font-size:  ${(props) => props.theme.fontmd};
text-transform: capitalize;
color:  ${props => `rgba(${props.theme.textRgba}, 0.6)`};
font-weight: 600;
margin-bottom: 1rem;
width: 100%;
align-self: flex-start;
text-align: center;
text-align: justify;
// position: relative;
`;

const Detail2 = () => {
  return (
    <>
    
    <VideoContainer>
        <LottieAnimation animationData={animationData} id="detail2" speed={0.5}></LottieAnimation>
    </VideoContainer>
    <TextContainer>
        <Title>Animated Content</Title>
        <br></br>
        <SubTitle>Our texts are backed up with colorful and lively animated videos which encourages learning by visualisation.</SubTitle>

    </TextContainer>
    </>
  )
}

export default Detail2
