import React from 'react'

import 'katex/dist/katex.min.css';
import { InlineMath, BlockMath } from 'react-katex';
import styled from 'styled-components';
import LottieAnimation from '../../../lottieScripts/LottieAnimation';
import animationData from '../../../assets/LottieJson/2Intro.json'

const VideoContainer = styled.div`
width: 80%;
display: flex;
align-items: center;

@media (max-width: 64em) {
  min-width: 27vh;
}
@media (max-width: 48em) {
  min-width: 24vh;
}
@media (max-width: 40em) {
  min-width: 18vh;
}

`

const IntroContent = () => {
    
  const inlineFormula = '\\cos (2\\theta) = \\cos^2 \\theta - \\sin^2 \\theta';
  const blockFormula = `\\frac{n!}{k!(n-k)!} = \\binom{n}{k}`;
  return (
    <VideoContainer>
        <LottieAnimation animationData={animationData} id="2Intro"></LottieAnimation>
    </VideoContainer>
  )
}

export default IntroContent
