import React from 'react'

import Box from '@mui/material/Box';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import StepContent from '@mui/material/StepContent';
import Button from '@mui/material/Button';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
    typography: {
        fontFamily: [
            'Poppins',
            'sans-serif'
        ].join(','),
    },
    components: {
      MuiButton: {
        styleOverrides: {
          root: ({ ownerState }) => ({
            ...(ownerState.variant === 'contained' &&
              ownerState.color === 'primary' && {
                backgroundColor: 'rgb(247, 147, 29)',
                color: 'white',
                '&:hover': {
                  backgroundColor: '#FF931D',
                  color: '#fff',
              },
              }),
              ...((ownerState.variant === 'back' || ownerState.variant === 'reset') &&
                ownerState.color === 'primary' && {
                  backgroundColor: '#fff',
                  color: '#F7931D',
                  '&:hover': {
                    backgroundColor: 'rgba(247, 147, 29, 0.1)',
                    color: '#F7931D',
                },
                }),
          }),
        },
      },
      MuiStepIcon: {
       root: {
         '&$completed': {
           color: 'pink',
         },
         '&$active': {
           color: 'red',
         },
       },
       active: {},
       completed: {},
     },
      MuiStepper: {
        styleOverrides: {
          root: ({ ownerState }) => ({
            "& .Mui-active .MuiStepIcon-root": { color: "#F7931D" },
            "& .Mui-completed .MuiStepIcon-root": { color: "#F7931D" },
            "& .Mui-disabled .MuiStepIcon-root": { color: "grey" },
            '&$completed': {
              color: 'pink',
            },
            '&$active': {
              color: 'red',
            },
            active: {},
            completed: {},
            iconColor: 'rgba(247, 147, 29, 0.1)',
            color: 'black',
          }),
        },
      },
    },
});

const ExpStepper = ({steps}) => {
    const [activeStep, setActiveStep] = React.useState(0);

    const handleNext = () => {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
    };

    const handleBack = () => {
        setActiveStep((prevActiveStep) => prevActiveStep - 1);
    };

    const handleReset = () => {
        setActiveStep(0);
    };
  return (
        <ThemeProvider theme={theme}>
        <Typography gutterBottom variant="h5" component="div" align="left" sx={{ fontWeight: 'bold'}}>Experiment 1</Typography>
            <Box sx={{ maxWidth: 400 }}>
                <Stepper  activeStep={activeStep} orientation="vertical" sx={{ iconc: 'bold'}}>
                    {steps.map((step, index) => (
                        <Step key={step.label}>
                            <StepLabel 
                            >
                                {step.label}
                            </StepLabel>
                            <StepContent>
                                <Typography>{step.description}</Typography>
                                <Box sx={{ mb: 2 }}>
                                    <div>
                                        <Button
                                            variant="contained"
                                            onClick={handleNext}
                                            sx={{ mt: 1, mr: 1}}
                                        >
                                            {index === steps.length - 1 ? 'Finish' : 'Continue'}
                                        </Button>
                                        <Button
                                            variant="back"
                                            disabled={index === 0}
                                            onClick={handleBack}
                                            sx={{ mt: 1, mr: 1}}
                                        >
                                            Back
                                        </Button>
                                    </div>
                                </Box>
                            </StepContent>
                        </Step>
                    ))}
                </Stepper>
                {activeStep === steps.length && (
                    <Paper square elevation={0} sx={{ p: 3 }}>
                        <Typography>Wanna try again!</Typography>
                        <Button variant="reset" onClick={handleReset} sx={{ mt: 1, mr: 1 }}>
                        <Typography>Reset</Typography>
                            
                        </Button>
                    </Paper>
                )}
            </Box>
        </ThemeProvider>
  )
}

export default ExpStepper
