import React from 'react'

import styled from 'styled-components';
import LottieAnimation from '../../../lottieScripts/LottieAnimation';
import animationData from '../../../assets/LottieJson/2Intro.json'

const VideoContainer = styled.div`
width: 100%;
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
`

const AnimatedContainer = styled.div`
width: 39%;
`

const TextContainer = styled.div`
width: 60%;
display: flex;
flex-direction: column;
justify-content:left;
text-align: justify;
`

const IntroContent = () => {
    
  return (
    <VideoContainer>
        <AnimatedContainer>
            <LottieAnimation animationData={animationData} id="Intro"></LottieAnimation>
        </AnimatedContainer>
        <TextContainer>
            <p><b>Gravitational force</b> is one of the four major forces (the other forces are Electromagnetic force, weak nuclear force and strong nuclear force). <b>It is particularly responsible for the invisible attraction among entities that have a property called mass.</b></p>
        </TextContainer>
    </VideoContainer>
  )
}

export default IntroContent
