import React from 'react'
import styled from 'styled-components'
import Typewriter from 'typewriter-effect';

const Title = styled.h2`
font-size: ${props => props.theme.fontxxl};
text-transform: capitalize;
width: 80%;
color: ${props => props.theme.text};
align-self: flex-start;

span{
    text-transform: capitalize;
    font-family: Poppins;
}
.text-1{
    color: ${props => props.theme.logoColor};
}
.text-2{
    color: ${props => props.theme.myBlue};
}
.text-3{
    color: ${props => props.theme.myGreen};
}
`;

const SubTitle = styled.h3`
font-size:  ${props => props.theme.fontlg};
text-transform: capitalize;
color:  ${props => `rgba(${props.theme.textRgba}, 0.6)`};
`

const TypeWriterText = () => {
  return (
    <>
        <Title>
        Learning
        <Typewriter
        options={{
            autoStart: true,
            loop: true,
        }}
  onInit={(typewriter) => {
    typewriter.typeString(`<span class="text-1">Exemplified.</span>`)
    .pauseFor(2000)
    .deleteAll()
    .typeString(`<span class="text-2">Visualized.</span>`)
    .pauseFor(2000)
    .deleteAll()
    .typeString(`<span class="text-3">in a new way.</span>`)
    .pauseFor(2000)
    .deleteAll()
    .start();
  }}
    />
    </Title>
    <SubTitle>Dive into the wonderland of curated examples.</SubTitle>
    </>

  )
}

export default TypeWriterText
