import React from 'react'
import styled from 'styled-components'
import Detail1 from '../details/Detail1'
import Detail2 from '../details/Detail2'
import Detail3 from '../details/Detail3'
import Detail4 from '../details/Detail4'

const Section = styled.section`
min-height: 80vh;
width: 100%;
background-color: ${props => props.theme.lightBody};
display: flex;
justify-content: center;
align-items: center;
position: relative;
`
const Container = styled.div`
width: 75%;
margin: 0 auto;

display: flex;
justify-content: center;
align-items: center;
`

const Box = styled.div`
width: 25%;
height: 100%;
max-height: 75vh;
display: flex-start;
flex-direction: coloumn;
justify-content: center;
align-items: center;
position: relative;
`
const TextContainer = styled.div`
width: 80%;
height:30vh;
`

const Title = styled.h2`
font-size: ${(props) => props.theme.fontlg};
text-transform: capitalize;
width: 100%;
color: ${(props) => props.theme.text};
text-align: center;
// position: relative;
`;

const Details = () => {
  return (
    <Section>
    <TextContainer>
        <Title>In-Depth Explainations</Title>
        <br></br>
    </TextContainer>
      
      <Container>
        <Box> <Detail1 /> </Box>
        <Box> <Detail2 /> </Box>
        <Box> <Detail3 /> </Box>
        <Box> <Detail4 /> </Box>
        
        
      </Container>
      
    </Section>
  )
}

export default Details
