import React from 'react'
import styled from 'styled-components'
import creative from '../../assets/About/creative.svg'

const Section = styled.section`
// min-height: 80vh;
width: 100%;
background-color: ${props => props.theme.body};
//display: flex;
justify-content: center;
align-items: center;
position: relative;
`
const TextContainer = styled.div`
width: 100%;
height:15vh;
background-color: ${props => props.theme.body};
display: flex;
justify-content: center;
align-items: center;

@media (max-width: 70em) {
height:10vh;
}
@media (max-width: 48em) {
height:5vh;
}
@media (max-width: 40em) {
height:5vh;
}
`
const ContentContainer = styled.div`
width: 80%;
height:20vh;
background-color: ${props => props.theme.body};
display: flex;
justify-content: center;
align-items: center;

@media (max-width: 70em) {
height:10vh;
}
@media (max-width: 48em) {
height:5vh;
}
@media (max-width: 40em) {
height:5vh;
}
`

const Title = styled.h1`
font-size: ${(props) => props.theme.fontxxl};
text-transform: capitalize;
color: ${(props) => props.theme.text};
display: flex;
justify-content: center;
align-items: center;

@media (max-width: 70em) {
    width: 90%;
font-size: ${(props) => props.theme.fontxl};
}
@media (max-width: 48em) {
font-size: ${(props) => props.theme.fontlg};
align-self: center;
text-align: center;
}
@media (max-width: 40em) {
    width: 90%;
}
`

const Content = styled.h2`
width: 80%;
font-size: ${(props) => props.theme.fontxl};
text-transform: capitalize;
color: ${(props) => props.theme.text};
display: flex-start;
justify-content: flex-start;
align-items: flex-start;
text-align: center;
text-align: justify;

@media (max-width: 70em) {
    width: 90%;
font-size: ${(props) => props.theme.fontlg};
}
@media (max-width: 48em) {
font-size: ${(props) => props.theme.fontmd};
align-self: center;
text-align: center;
}
@media (max-width: 40em) {
    width: 90%;
}
`

const Container = styled.div`
width: 80%;
min-height: 50vh;
margin: 0 auto;

display: flex;
justify-content: center;
align-items: center;

@media (max-width: 64em) {
  width: 85%;
min-height: 60vh;
}
@media (max-width: 48em) {
  flex-direction: column-reverse;
  width: 100%;

  &>*:first-child{
    width: 100%;
    margin-top: 2rem;
  }
}
`

const Box = styled.div`
width: 50%;
height: 100%;
// max-height: 75vh;
display: flex-start;
flex-direction: coloumn;
justify-content: center;
align-items: flex-start;
/* position: relative; */
`
const ImageContainer = styled.div`
width: 100%;
display: flex;
justify-content: center;
align-items: center;

img{
    width: 70%;
    height: auto;
    margin-bottom: 3rem;

}
`

const AboutText = () => {
  return (
    <Section>
        <TextContainer>
            <Title>About Us</Title>
        </TextContainer>
        <TextContainer>
            <Title>Learning doesn't have to be excruciating, it can also be fun.</Title>
        </TextContainer>
        <Container>
            <Content>Here at Silly Science, we try to bring back the fun in learning. We make maths and physics content using clear explanatory texts. Every line is curated by us, aiming to provide quality content. Our texts are backed up with colourful and lively animated videos. These videos stimulate learning by visualisation.</Content>
            
        </Container>
    </Section>
  )
}

export default AboutText
