import React from 'react'
import { useWindowScroll } from 'react-use';
import styled from 'styled-components'
import { useRef, useLayoutEffect } from 'react'
import { Link } from 'react-router-dom';
import { Typography } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Alert from '@mui/material/Alert';
import Stack from '@mui/material/Stack';

const theme = createTheme({
    typography: {
        fontFamily: [
            'Poppins',
            'sans-serif'
        ].join(','),
    }
});
theme.typography.h4 = {
    fontSize: '1.5em',
    [theme.breakpoints.up('lg')]: {
        fontSize: '1.5em'
    },
    [theme.breakpoints.down('lg')]: {
        fontSize: '1.5em'
    },
    [theme.breakpoints.down('md')]: {
        fontSize: '1em'
    },
    [theme.breakpoints.down('sm')]: {
        fontSize: '0.875em'
    },
    [theme.breakpoints.down('xs')]: {
        fontSize: '0.75em'
    },
};

theme.typography.h5 = {
    fontSize: '1em',
    [theme.breakpoints.up('lg')]: {
        fontSize: '1em'
    },
    [theme.breakpoints.down('lg')]: {
        fontSize: '1em'
    },
    [theme.breakpoints.down('md')]: {
        fontSize: '0.875em'
    },
    [theme.breakpoints.down('sm')]: {
        fontSize: '0.75em'
    },
    [theme.breakpoints.down('xs')]: {
        fontSize: '0.6em'
    },
};

var noteStyle = {
    width: '50vw',
    [theme.breakpoints.down('lg')]: {
        width: '15vw',
    },
    [theme.breakpoints.down('md')]: {
        width: '15vw',
    },
    [theme.breakpoints.down('sm')]: {
        width: '10vw',
    }
};

const AlertContainer = styled.div`
width: 100%;
position: relative;
display: flex;
text-align: justify;
justify-content: center;
padding-bottom: 3vw;
/* @media (max-width: 70em) {
  width: 30vw;
}
@media (max-width: 48em) {
  width: 34vw;
} */
`

const Left = styled.div`
/* width: 10%; */
/* line-height: 55px; */

box-sizing: border-box;
margin: 0;
padding: 0;
color: ${props => props.theme.text};
background-color: ${props => props.theme.logoColor};
/* opacity: 50%; */

font-size: ${props => props.theme.fontxl};
position: fixed;
right: 10%;
bottom: 50%;

cursor: pointer;

display: flex;
justify-content:center;

/* border-radius: 50%; */
transition: all 0.3s ease;
&:hover{
    transform: scale(1.2);
}
&:active{
    transform: scale(0.9);
}

@media (max-width: 70em) {
width: 40px;
height: 40px;
line-height: 35px;
}
`

const Next = ({link}) => {

  // const ref = useRef(null);
  // const { y } = useWindowScroll();

  // useLayoutEffect(() => {
  //   if (y > document.getElementById("myTopic").scrollHeight + document.getElementById("mySection").scrollHeight - document.getElementById("banner").scrollHeight) {
  //     ref.current.style.display = "none"
  //   } else {
  //     ref.current.style.display = "flex"
  //   }
  // }, [y])
  return (
    <Link underline='none' to={link}>
      <Left >
        
      
      <AlertContainer>
                <Stack style={noteStyle} spacing={2}>
                    <Alert icon={false} severity="warning">
                        <Typography gutterBottom variant="h5" component="div" align="left" sx={{ textAlign: 'justify' }}>
                            Lesson 2
                        </Typography>

                    </Alert>
                </Stack>
            </AlertContainer>
        <Typography>
        Lesson 2
        </Typography>
        
         &#x2192; 
      </Left>
    </Link>
  )
}

export default Next
