import React from 'react'
import { useWindowScroll } from 'react-use';
import styled from 'styled-components'
import { useRef, useLayoutEffect } from 'react'
import { Link } from 'react-router-dom';
import { Typography } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Alert from '@mui/material/Alert';
import Stack from '@mui/material/Stack';

const Left = styled.div`
/* width: 10%; */
/* line-height: 55px; */

box-sizing: border-box;
margin: 0;
padding: 0;
color: ${props => props.theme.text};
background-color: ${props => props.theme.logoColor};
opacity: 50%;

font-size: ${props => props.theme.fontxl};
position: fixed;
right: 10%;
bottom: 50%;

cursor: pointer;

display: flex;
justify-content:center;

/* border-radius: 50%; */
transition: all 0.3s ease;
&:hover{
    transform: scale(1.2);
}
&:active{
    transform: scale(0.9);
}

@media (max-width: 70em) {
width: 40px;
height: 40px;
line-height: 35px;
}
`

const Next = ({link}) => {

  // const ref = useRef(null);
  // const { y } = useWindowScroll();

  // useLayoutEffect(() => {
  //   if (y > document.getElementById("myTopic").scrollHeight + document.getElementById("mySection").scrollHeight - document.getElementById("banner").scrollHeight) {
  //     ref.current.style.display = "none"
  //   } else {
  //     ref.current.style.display = "flex"
  //   }
  // }, [y])
  return (
    <Link underline='none' to={link}>
      
      <AlertContainer>
                <Stack style={noteStyle} spacing={2}>
                    <Alert icon={false} severity="info">
                        <Typography gutterBottom variant="h4" component="div" align="left">
                            Note to Teacher
                        </Typography>
                        <Typography gutterBottom variant="h5" component="div" align="left" sx={{ textAlign: 'justify' }}>
                            {text}
                        </Typography>

                    </Alert>
                </Stack>
            </AlertContainer>
      {/* <Left >
        <Typography>
        Lesson 2
        </Typography>
        
         &#x2192; 
      </Left> */}
    </Link>
  )
}

export default Next
