import React from 'react'
import styled from 'styled-components'
import animationData from "../../assets/LottieJson/details/detail3.json";
import LottieAnimation from "../../lottieScripts/LottieAnimation";


const VideoContainer = styled.div`
width: 80%;
`

const Title = styled.h2`
font-size: ${(props) => props.theme.fontxl};
text-transform: capitalize;
width: 100%;
color: ${(props) => props.theme.text};
text-align: center;
position: relative;
`;

const SubTitle = styled.h3`
font-size:  ${(props) => props.theme.fontlg};
text-transform: capitalize;
color:  ${props => `rgba(${props.theme.textRgba}, 0.6)`};
font-weight: 600;
margin-bottom: 1rem;
width: 100%;
align-self: flex-start;
text-align: center;
text-align: justify;
position: relative;
`;

const Detail3 = () => {
  return (
    <>
        
    <VideoContainer>
        <LottieAnimation animationData={animationData} id="detail3" speed={0.5}></LottieAnimation>
    </VideoContainer>
        <Title>Illustrative Examples</Title>
        <br></br>
        <SubTitle>Every section of an explaination contains visual illustration to make every concept lucid.</SubTitle>
    </>
  )
}

export default Detail3
