import React from 'react'
import styled from 'styled-components'
import animationData from "../../assets/LottieJson/details/detail4.json";
import LottieAnimation from "../../lottieScripts/LottieAnimation";


const VideoContainer = styled.div`
width: 80%;
`
const TextContainer = styled.div`
width: 80%;
`

const Title = styled.h2`
font-size: ${(props) => props.theme.fontxl};
text-transform: capitalize;
width: 100%;
color: ${(props) => props.theme.text};
text-align: center;
position: relative;
`;

const Detail4 = () => {
  return (
    <>
    <VideoContainer>
        <LottieAnimation animationData={animationData} id="detail4" speed={0.5}></LottieAnimation>
        <Title>Curiosity Driven</Title>
    </VideoContainer>
    <TextContainer>
        <br></br>
        <SubTitle>Every section of an explaination contains visual illustration to make every concept lucid.</SubTitle>
    </TextContainer>
    
    </>
  )
}

export default Detail4
