import React from 'react'

import styled from 'styled-components';
import Markdown from "react-markdown";
import remarkMath from "remark-math";
import rehypeKatex from "rehype-katex";
import rehypeRaw from "rehype-raw";

const Section = styled.section`
width: 100%;
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
`

const TextContainer = styled.div`
width: 50%;
display: flex;
flex-direction: column;
justify-content:left;
text-align: justify;
line-height: 1.5;
font-size: ${props => props.theme.fontmd};

@media (max-width: 900px) {
    width:80%;
font-size: ${props => props.theme.fontsm};
}
@media (max-width: 600px) {
    width:80%;
font-size: ${props => props.theme.fontxs};
}
`
const content = `
<ul>
\n &nbsp;
\n
<li><b>Gravitational force</b> is responsible for the invisible attraction among entities that have a property called mass.</li>
\n &nbsp;
\n
<li>Density</li>
\n &nbsp;
\n
<li>Density</li>
\n &nbsp;
\n
<li>None</li>
\n &nbsp;
\n
<li>None</li>
\n &nbsp;
\n
<li>None</li>
\n &nbsp;
\n
<li>None</li>
\n &nbsp;
\n
<li>None</li>
\n &nbsp;
\n
<li>None</li>
</ol>
`;

const SummaryContent = () => {
  return (
    <Section>
        <TextContainer>
            <Markdown remarkPlugins={[remarkMath]} rehypePlugins={[rehypeKatex, rehypeRaw]}>{content}</Markdown>
        </TextContainer>
    </Section>
  )
}

export default SummaryContent
