import React from 'react'
import Accordion from '@mui/material/Accordion';
import AccordionSummary from '@mui/material/AccordionSummary';
import AccordionDetails from '@mui/material/AccordionDetails';
import Typography from '@mui/material/Typography';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import styled from 'styled-components';
import Markdown from "react-markdown";
import remarkMath from "remark-math";
import rehypeKatex from "rehype-katex";
import rehypeRaw from "rehype-raw";

const theme = createTheme({
    typography: {
        fontFamily: [
            'Poppins',
            'sans-serif'
        ].join(','),
    }
});
theme.typography.h4 = {
    fontSize: '1.5em',
    [theme.breakpoints.up('lg')]: {
        fontSize: '1.5em'
    },
    [theme.breakpoints.down('lg')]: {
        fontSize: '1.5em'
    },
    [theme.breakpoints.down('md')]: {
        fontSize: '1em'
    },
    [theme.breakpoints.down('sm')]: {
        fontSize: '0.875em'
    },
    [theme.breakpoints.down('xs')]: {
        fontSize: '0.75em'
    },
};

theme.typography.h5 = {
    fontSize: '1em',
    [theme.breakpoints.up('lg')]: {
        fontSize: '1em'
    },
    [theme.breakpoints.down('lg')]: {
        fontSize: '1em'
    },
    [theme.breakpoints.down('md')]: {
        fontSize: '0.875em'
    },
    [theme.breakpoints.down('sm')]: {
        fontSize: '0.75em'
    },
    [theme.breakpoints.down('xs')]: {
        fontSize: '0.6em'
    },
};

const AccordianDiv = styled.div`
padding: 1%;
`


const QnA = ({ srno, question, answer }) => {
    return (
        <AccordianDiv>
            <ThemeProvider theme={theme}>
                <Accordion >
                    <AccordionSummary
                        expandIcon={<ExpandMoreIcon />}
                        aria-controls="panel1a-content"
                        id="panel1a-header"
                    >
                        <Typography gutterBottom variant="h5" component="div" align="left" sx={{ fontWeight: 'bold', textAlign: 'justify', minWidth: '3vw', maxWidth: '3vw'}}>{srno}.</Typography>
                        <Typography gutterBottom variant="h5" component="div" align="left" sx={{ fontWeight: 'bold', textAlign: 'justify', marginRight: '3%' }}>
                        <Markdown remarkPlugins={[remarkMath]} rehypePlugins={[rehypeKatex, rehypeRaw]}>{question}</Markdown>
                        </Typography>
                    </AccordionSummary>
                    <AccordionDetails>
                        <Typography gutterBottom variant="h5" component="div" align="left" sx={{ textAlign: 'justify', marginLeft: '3%', marginRight: '5.7%' }}>
                        <Markdown remarkPlugins={[remarkMath]} rehypePlugins={[rehypeKatex, rehypeRaw]}>{answer}</Markdown>
                        </Typography>
                    </AccordionDetails>
                </Accordion>
            </ThemeProvider>
        </AccordianDiv>
    )
}

export default QnA
